import Product from "./Product"
import img1 from "../img/epices_viande.jpg"

import { productData } from "../data/productData"

export default function ProductGroup(){
  const productList = productData
  return(
      <div className="container">
      <div className="text-center title"><h1>Nos produits</h1></div>
      <div className="row">
        {productList.map(p => (
          <div className="col align-self-center"><Product key={p.id} productName={p.productName} productDesc={p.productDesc} productImg={img1}/></div>
          )
          )
        }
      </div>
    </div>
  )
}
